mapper.select:
select
,floor_name,biz_format_type
from biz_shop
JOIN biz_floor USING(floor_id)
JOIN biz_format USING (biz_format_id)

    and shop_name like concat('%',#{shopName},'%')
    and shop_house_number like concat('%',#{shopHouseNumber},'%')
    and is_shop_enabled=#{shopName}
    and floor_id=#{floorId}
    and biz_format_id=#{bizFormatId}

SELECT bs.shop_id FROM biz_shop bs
JOIN biz_format bf USING (biz_format_id)
WHERE bf.biz_format_type=#{bizFormatType}
