mapper.select:
SELECT
,floor_name
FROM biz_terminal
JOIN biz_floor USING (floor_id)

    is_deleted=1
    and terminal_name like concat('%',#{terminalName},'%')
    and terminal_number like concat('%',#{`},'%')
    and terminal_type =#{terminalType}
    and is_terminal_online=#{isTerminalOnline}
    and floor_id=#{floorId}
